import axios from 'axios'
import  { useEffect, useState } from 'react'

const useFriendlist = () => {

    const [data, setdata] = useState([])
    const [isLoading, setisLoading] = useState(true)
    const [error, seterror] = useState()

    const getFriends = async () => { 
        try {
            setisLoading(true)
            seterror()

            const result = await axios.get('http://localhost:5001/user');
            if(result){
                const friendList = result.data.data.map((friend)=> {
                    friend.notif = null ;
                    return friend
                })
                setdata(friendList)
            }
        } catch (error) {
            console.log('error in get message',error.response.data)
            console.log(error)
            seterror()
        }finally{
            setisLoading(false)
        }
     }


     useEffect(() => {

        getFriends()
     }, [])

  return {
    data,
    isLoading,
    error

  }
}

export default useFriendlist
