import axios from 'axios'
import { useEffect, useState } from 'react'
import { socket } from '../../../socket'

const useFriendlist = () => {



    const [data, setdata] = useState([])
    const [isLoading, setisLoading] = useState(true)
    const [error, seterror] = useState()

    const getFriends = async () => {
        try {
            setisLoading(true)
            seterror()

            const result = await axios.get('http://localhost:5001/user');
            if (result) {
                setdata(result.data.data)
            }
        } catch (error) {
            console.log('error in get message', error.response?.data)
            console.log(error)
            seterror('an error has occured')
        } finally {
            setisLoading(false)
        }
    }


    useEffect(() => {
        getFriends()

    }, [])

    useEffect(() => {

        // Socket receive
        function onMessageRec(data) {

        }
        socket.on('receive_online', onMessageRec);

        return () => {
            socket.off('receive_online', onMessageRec);
        }
    }, [])

    return {
        data,
        isLoading,
        error

    }
}

export default useFriendlist
