import axios from 'axios';
import { useState } from 'react'
import { useNavigate } from 'react-router-dom';

const useLogin = () => {

    const [password, setPassword] = useState("");
    const [email, setEmail] = useState("");
    const [passwordError, setpasswordError] = useState("");
    const [emailError, setemailError] = useState("");

    const navigate = useNavigate();

    const handleValidation = (event) => {
        let formIsValid = true;

        if (!email.match(/^[a-zA-Z_]{3,20}$/)) {
            formIsValid = false;
            setemailError("Username Not Valid");
            return false;
        } else {
            setemailError("");
            formIsValid = true;
        }

        if (!password.match(/^[a-zA-Z1-9]{2,22}$/)) {
            formIsValid = false;
            setpasswordError(
                " length must best min 2 Characters "
            );
            return false;
        } else {
            setpasswordError("");
            formIsValid = true;
        }

        return formIsValid;
    };

    const loginSubmit = (e) => {
        e.preventDefault();
        const valid = handleValidation();
        if (valid) {
            const result = axios.post('http://localhost:5001/user/signin', { name: email, password })
            if (result.statusCode == 7000) {
                localStorage.setItem('ACT', result.data)
                navigate('/home')
            };
            if (result.statusCode == 7001) {
                setemailError('User not Found')
            }
            if (result.statusCode == 7003) {
                setpasswordError('Invalid Password')
            }
        }
    };

    return {
        loginSubmit,
        setEmail,
        emailError,
        setPassword,
        passwordError
    }
}
export default useLogin
