import axios from 'axios'
import { useEffect, useRef, useState } from 'react'
import { socket } from '../../../socket'

const useFriendlist = () => {



    const [data, setdata] = useState([])
    const [onlineList, setOnlineList] = useState([])
    const onlineListRef = useRef(onlineList)
    const [isLoading, setisLoading] = useState(true)
    const [error, seterror] = useState()

    const getFriends = async () => {
        try {
            setisLoading(true)
            seterror()

            const result = await axios.get('http://localhost:5001/user');
            if (result) {
                setdata(result.data.data)
            }
        } catch (error) {
            console.log('error in get message', error.response?.data)
            console.log(error)
            seterror('an error has occured')
        } finally {
            setisLoading(false)
        }
    }


    useEffect(() => {
        getFriends()

    }, [])



    useEffect(() => {

        // Socket receive
        function onOnlineRec(online) {
            setOnlineList(online)
            onlineListRef.current = online

        }

        socket.on('receive_online', onOnlineRec);
        console.log('socket =>', socket)

    }, [])

    return {
        data,
        isLoading,
        error,
        onlineList,
        onlineListRef

    }
}

export default useFriendlist
