import axios from 'axios'
import { useEffect, useState } from 'react'
import { socket } from '../../../socket'

const useFriendlist = () => {



    const [data, setdata] = useState([])
    const [isLoading, setisLoading] = useState(true)
    const [error, seterror] = useState()

    const getFriends = async () => {
        try {
            setisLoading(true)
            seterror()

            const result = await axios.get('http://localhost:5001/user');
            if (result) {
                setdata(result.data.data)
            }
        } catch (error) {
            console.log('error in get message', error.response?.data)
            console.log(error)
            seterror('an error has occured')
        } finally {
            setisLoading(false)
        }
    }


    useEffect(() => {
        getFriends()

    }, [])


    function onOnlineRec(online) {
        // alert('hoy')
        console.log('online',online)
        console.log('data',data)
        if (data && data.length !== 0) {
            console.log('new list',data.map((friend) => {
                if (online.indexOf(friend.id) !== -1) {
                    friend.online = true;
                    return friend;
                } else {
                    friend.online = false;
                    return friend;
                }
            }))
            setdata(data.map((friend) => {
                if (online.indexOf(friend.id) !== -1) {
                    friend.online = true;
                    return friend;
                } else {
                    friend.online = false;
                    return friend;
                }
            }))
        }
    }
    useEffect(() => {

        // Socket receive


        socket.on('receive_online', onOnlineRec);

        return () => {
            socket.off('receive_online', onOnlineRec);
        }
    },[data])

    return {
        data,
        isLoading,
        error

    }
}

export default useFriendlist
