import axios from 'axios'
import { useContext, useEffect, useState } from 'react'

import dayjs from 'dayjs'
import { AppAuthContext } from '../../../core/contexts/AppAuthProvider'

const useChat = (friend) => {

    const [messages, setmessages] = useState([])
    const [isLoading, setisLoading] = useState(true)
    const [error, seterror] = useState()
    const [msgLoading, setmsgLoading] = useState(false)
    const [msgError, setmsgError] = useState()
    const [msg, setMsg] = useState();



    const {   authenticatedUser : user } = useContext(AppAuthContext)
    console.log(user.id)

    const getMessages = async () => {
        try {
            setisLoading(true)
            seterror()

            const result = await axios.get('http://localhost:5001/message', { params: { user : user.id , friend } });
            if (result && result.length != 0) {
                setmessages(result.data.data)
            }
        } catch (error) {
            if (error.response?.data.statusCode == 7004) {
                setmessages([])
            } else seterror('An error has occured')
        } finally {
            setisLoading(false)
        }
    };

    const sendMessage = async () => {
        try {
            setmsgLoading(true)
            setmsgError()

            const result = await axios.post('http://localhost:5001/message', { sender: user.id, receiver: friend, content: msg, date: dayjs() });
            if (result && result.length != 0) {
                console.log(result.data.data)
                setMsg()
                setmessages(previous => [result.data.data, ...previous ])
            }
        } catch (error) {
            console.log(error);
            setmsgError("An error has occured")
        } finally {
            setmsgLoading(false)
        }
    }

    const handleSend = (e) => {
        e.preventDefault();
        msg && sendMessage();

    }
    useEffect(() => {
        getMessages()
    }, [friend])

    return {
        messages,
        isLoading,
        error,
        handleSend,
        setMsg,
        msgLoading,
        msgError,
        user

    }
}

export default useChat
