import { Navigate, Outlet } from 'react-router-dom';
import { useContext } from 'react';
import { AppAuthContext } from '../core/contexts/AppAuthProvider';
import { decodeJwt } from 'jose';

const ProtectedRoute = ({ children }) => {

  const { userToken, setAuthenticatedUser } = useContext(AppAuthContext);

  if (!userToken) return <Navigate to="/signin" />
  const decoded = decodeJwt(userToken, 'barabaw');
  setAuthenticatedUser(decoded);
  return children ? children : <Outlet />;
};

export default ProtectedRoute;
