import React from 'react'
import { Alert, Spinner } from 'react-bootstrap'

const ChatContent = ({ messages, error, isLoading }) => {
    // let messages = [{sender:1},{sender:1},{sender:2},{sender:1},{sender:2},{sender:1},{sender:1},{sender:2},{sender:1},{sender:1},{sender:1},]
    const friend = { id: 1 }
    const user = { id: 2 }
    let lastFromFriend = false


    if (isLoading && !error) return <div className='d-flex justify-content-center align-items-center w-100 h-100'><Spinner /></div>
    if (!isLoading && error) return <Alert> {error} </Alert>
    if (messages && messages.length !== 0) return (
        messages.map((message, idx) => {
            console.log(message)
            if (message.sender == user.id) {
                return (
                    <div className={`p-2 d-flex flex-column align-items-end `}>
                        <div className='border p-2 rounded-5'>
                            {message.content}
                        </div>
                    </div>
                )
            }
            if (message.sender == friend.id) {
                if (idx != 0) (messages[idx - 1].sender == friend.id) ? lastFromFriend = true : lastFromFriend = false

                return (
                    <div className={`p-2 d-flex flex-column align-items-start `}>

                        {(!lastFromFriend) && <h6>friend : </h6>}

                        <div className='border p-2 rounded-5'>
                            {message.content}
                        </div>
                    </div>
                )
            }

        })
    )
}

export default ChatContent
