import { useState } from "react";

const useNotification = (key, defaultValue = null) => {

    const [storedValue, setStoredValue] = useState(
        () => {
            try {
                const value = window.localStorage.getItem(key);
                if (value) return JSON.parse(value);
                else {
                    ////window.localStorage.setItem(key, JSON.stringify(defaultValue));
                    return defaultValue;
                }
            } catch (err) {
                return defaultValue;
            }
        }
    );

    const setValue = (newValue) => {
        let notifList = []
        try {

            if (storedValue) {
                storedValue.findIndex()
                const idx = storedValue.findIndex((elmnt) => elmnt.id == newValue);

                console.log('idx' , idx)

                if (idx) {
                    console.log('id exist')
                    const newNotif = { id: newValue, count: storedValue[idx].count }
                    notifList = storedValue.splice(idx, 1)
                    notifList.push(newNotif)

                } else if (!idx) {
                    console.log('new notif id')
                    notifList.push({ id: newValue, count: 1 })
                }
            }

            console.log('after treatment : ', notifList)

            if (newValue) window.localStorage.setItem(key, JSON.stringify(notifList));
            else window.localStorage.removeItem(key);
        } catch (err) { }
        setStoredValue(notifList);
    };

    return [storedValue, setValue];
};

export default useNotification;
