import axios from 'axios'
import React, { useEffect, useState } from 'react'

const useChat = (friend) => {

    const [messages, setmessages] = useState([])
    const [isLoading, setisLoading] = useState(true)
    const [error, seterror] = useState()
    const [msgLoading, setmsgLoading] = useState(true)
    const [msgError, setmsgError] = useState()
    const [msg,setMsg] = useState();


    let user = 2


    const getMessages = async () => {
        try {
            setisLoading(true)
            seterror()

            const result = await axios.get('http://localhost:5001/message', { params: { user, friend } });
            if (result && result.length != 0) {
                setmessages(result.data.data)
            }
        } catch (error) {
            if (error.response.data.statusCode == 7004) {
                setmessages([])
            } else seterror('An error has occured')
        } finally {
            setisLoading(false)
        }
    };

    const sendMessage = async () => { 
        try {
            setmsgLoading(true)
            setmsgError()

            const result = await axios.post('http://localhost:5001/message', { params: { user, friend } });
            if (result && result.length != 0) {
                setmessages(result.data.data)
            }
        } catch (error) {
            console.log(error);
            setmsgError("An error has occured")
        }
     }

    const handleSend = (e,data) => {
        console.log(e.target.value)
        e.preventDefault();
        sendMessage();

    }
    useEffect(() => {
        getMessages()
    }, [friend])

    return {
        messages,
        isLoading,
        error,
        handleSend,
        setMsg,
        msgLoading,
        msgError

    }
}

export default useChat
