import { createContext, useEffect, useState } from "react";
import useLocalStorage from "../hooks/useLocalStorage";
import {decodeJwt} from 'jose'
export const AppAuthContext = createContext();

const AppAuthProvider = ({ children }) => {

  const [userToken, setUserToken] = useLocalStorage('ACT');
  const [authenticatedUser, setAuthenticatedUser] = useState();

  const providerValue = {
    userToken,
    setUserToken,
    authenticatedUser,
    setAuthenticatedUser
  }

  useEffect(() => {
    const decoded = decodeJwt(userToken,'barabaw')
    setAuthenticatedUser(decoded);

  }, [userToken])


  return (
    <AppAuthContext.Provider value={ providerValue }>
      {children}
    </AppAuthContext.Provider>
  )
};

export default AppAuthProvider;
